insert into drugs.restriction (pbscode, code, streamlined, restriction) values ('9174W','2980','f',$$<p>Initial PBS-subsidised treatment of a patient with hypereosinophilic syndrome or chronic eosinophilic leukaemia requiring treatment and confirmed to carry the FIP1L1-PDGFRA fusion gene.</p><p> Maximum dose: 400&#160;mg per day.</p><p> Applications for authorisation for initial treatment must be made in writing and must include:<br/> (a) a completed authority prescription form; and<br/> (b) a completed Rare Diseases Imatinib PBS Authority Application - Supporting Information Form; and<br/> (c) a copy of the pathology report confirming the presence of the FIP1L1-PDGFRA fusion gene; and<br/> (d) a copy of the full blood examination report confirming the presence of hypereosinophilic syndrome or chronic eosinophilic leukaemia; and<br/> (e) details of organ involvement requiring treatment, including a copy of the radiology, nuclear medicine, respiratory function or anatomical pathology reports as appropriate; and<br/> (f) a signed patient acknowledgement</p>$$);
