insert into drugs.restriction (pbscode, code, streamlined, restriction) values ('6423C','2620','f',$$<p>Active acromegaly in a patient with persistent elevation of mean growth hormone levels of greater than 2.5&#160;micrograms per litre AND<br/> (a) after failure of other therapy including dopamine agonists; or<br/> (b) as interim treatment while awaiting the effects of radiotherapy and where treatment with dopamine agonists has failed; or<br/> (c) if the patient is unfit for or unwilling to undergo surgery and where radiotherapy is contraindicated.<br/> In a patient treated with radiotherapy, treatment must cease if there is biochemical evidence of remission (normal IGF1) after lanreotide has been withdrawn for at least 4 weeks (8 weeks after the last dose). Lanreotide should be withdrawn every 2 years in the 10 years after radiotherapy for assessment of remission.<br/> Treatment must cease if IGF1 is not lower after 3 months treatment</p>$$);
