insert into drugs.restriction (pbscode, code, streamlined, restriction) values ('9393J','3088','f',$$<p>Treatment of severe primary Restless Legs Syndrome in a patient who manifests all 4 diagnostic criteria below and whose baseline International Restless Legs Syndrome Rating Scale (IRLSRS) score is greater than or equal to 21 points prior to initiation of pramipexole.</p><p> The date and IRLSRS score must be documented in the patient's medical records at the time pramipexole treatment is initiated.</p><p> The diagnostic criteria for Restless Legs Syndrome are:<br/> (a) An urge to move the legs usually accompanied or caused by unpleasant sensations in the legs; and<br/> (b) The urge to move or unpleasant sensations begin or worsen during periods of rest or inactivity such as lying or sitting; and<br/> (c) The urge to move or unpleasant sensations are partially or totally relieved by movement, such as walking or stretching, at least as long as the activity continues; and<br/> (d) The urge to move or unpleasant sensations are worse in the evening or night than during the day or only occur during the evening or night.</p><p> Pramipexole is not PBS-subsidised for Restless Legs Syndrome secondary to other causes</p>$$);
insert into drugs.restriction (pbscode, code, streamlined, restriction) values ('9394K','3088','f',$$<p>Treatment of severe primary Restless Legs Syndrome in a patient who manifests all 4 diagnostic criteria below and whose baseline International Restless Legs Syndrome Rating Scale (IRLSRS) score is greater than or equal to 21 points prior to initiation of pramipexole.</p><p> The date and IRLSRS score must be documented in the patient's medical records at the time pramipexole treatment is initiated.</p><p> The diagnostic criteria for Restless Legs Syndrome are:<br/> (a) An urge to move the legs usually accompanied or caused by unpleasant sensations in the legs; and<br/> (b) The urge to move or unpleasant sensations begin or worsen during periods of rest or inactivity such as lying or sitting; and<br/> (c) The urge to move or unpleasant sensations are partially or totally relieved by movement, such as walking or stretching, at least as long as the activity continues; and<br/> (d) The urge to move or unpleasant sensations are worse in the evening or night than during the day or only occur during the evening or night.</p><p> Pramipexole is not PBS-subsidised for Restless Legs Syndrome secondary to other causes</p>$$);
